//// ---------------------------------------------------------------------------// Assignment 4: Movie Store// Group Members: Kyle Williams(2550507) and Nora Osmanova(2550505)// Date: 08/12/2025//// Description:// Implement a hash table to look up customers, add and remove customers.// Has a vector bucket to hold customers by their ID number has keys and names/last// names as values. Can insert, find, remove and hash// ---------------------------------------------------------------------------//#ifndef HASHTABLE_H#define HASHTABLE_H#include <vector>#include <utility>template <typename KeyType, typename ValueType>;class HashTable{private:       //Attributes       std::vector<std::vector<std::pair<KeyType, ValueType>>> table;       int capacity;       int size;        //Private Method        int hash(const KeyType& key); //hashes the data into the hashtablepublic:       //Constructor and Destructor       HashTable(int capacity = 101)       ~HashTable();//Public Methodsbool insert(const KeyType& key, const ValueType& value);     //Inserts customers into the buckets by id number and nameValueType* find(const KeyType& key);                         // returns the customer name given the key. return nullptr if not foundbool remove(const KeyType& key);                             // removes customer given the key};#endif //HASHTABLE_H